query getAllBroadcastDataWithProducts($broadcastId: String!) {
  getAllBroadcastData(broadcastId: $broadcastId) {
    broadcast {
      ...BroadcastFragmentWithProducts
    }
    vodEvents {
      ...VodEventFragment
    }
  }
}

fragment BroadcastFragmentWithProducts on Broadcast {
  liveViewers
  isChatEnabled
  status
  chatStatus
  startTime
  moderated
  hlsUrl
  videoMetadata {
    customerId
    contentId
    sessionId
  }
  trimmingMetadata {
    trimmingEnabled
    hlsUrl
    secondsTrimmed
  }
  title
  pinnedMessage {
    text
    promoId
    status
    displayType
    message
  }
  promotions {
    associatedAsins
    description
    discountPercent
    groupClaimCode
    id
  }
  promoCodes {
    asin
    promoCodes
  }
  highlightedItemId
  segments {
    name
    segmentItems {
      segmentItemId
      type
      asin
      actionButtonText
      backgroundImageUrl
      badgeText
      imageUrl
      title
      url
      retargetingPixel
      revealData {
        time
        text
        subtext
        percent
      }
    }
  }
}

fragment VodEventFragment on VodEvent {
  eventOffsetMS
  eventType
  eventValue
}
